-- Setting NOCOUNT ON suppresses completion messages for each INSERT
SET NOCOUNT ON

-- Set date format to month, day, year
SET DATEFORMAT ymd;

-- Make the master database the current database
USE master

-- If database hasc exists, drop it
IF EXISTS (SELECT * FROM sysdatabases WHERE name = 'hasc')
BEGIN
  ALTER DATABASE hasc SET SINGLE_USER WITH ROLLBACK IMMEDIATE;
  DROP DATABASE hasc;
END
GO

-- Create the hasc database
CREATE DATABASE hasc;
GO

-- Make the hasc database the current database
USE hasc;

-- Create divisions table
CREATE TABLE divisions (
	division_id INT PRIMARY KEY,
	division_name VARCHAR(50),
	teams_made BIT); 

-- Create provinces table
CREATE TABLE provinces (
	province_id CHAR(2) PRIMARY KEY,
	province_name VARCHAR(50));

-- Create skills table
CREATE TABLE skills (
	skill_level CHAR(1) PRIMARY KEY ,
	skill_description VARCHAR(50));

-- Create teams table
CREATE TABLE teams (
	team_id INT PRIMARY KEY,
	team_name VARCHAR(50),
	jersey_colour VARCHAR(50),
	division_id INT FOREIGN KEY (division_id) REFERENCES divisions (division_id));

-- Create persons table
CREATE TABLE persons (
	person_id INT PRIMARY KEY,
	first_name VARCHAR(50),
	last_name VARCHAR(50),
	division_id INT FOREIGN KEY REFERENCES divisions (division_id),
	email VARCHAR(50),
	gender CHAR(1),
	birth_date DATE,
	address_line_1 VARCHAR(50),
	address_line_2 VARCHAR(50),
	city VARCHAR(50),
	province_id CHAR(2) FOREIGN KEY REFERENCES provinces (province_id),
	postal_code CHAR(7),
	phone VARCHAR(20),
	player BIT,
	skill_level CHAR(1) FOREIGN KEY REFERENCES skills (skill_level),
	team_id INT FOREIGN KEY REFERENCES teams (team_id),
	jersey_number INT,
	coach BIT,
	coaching_experience VARCHAR(500),
	referee BIT,
	referee_experience VARCHAR(500),
	administrator BIT,
	user_password VARCHAR(20));

-- Create games table
CREATE TABLE games (
	game_id INT PRIMARY KEY,
	game_date DATE,
	field VARCHAR(50),
	home_team_id INT  FOREIGN KEY REFERENCES teams (team_id),
	home_team_score INT,
	away_team_id INT  FOREIGN KEY REFERENCES teams (team_id),
	away_team_score INT,
	referee_id INT FOREIGN KEY REFERENCES persons (person_id),
	game_notes VARCHAR(1000));
GO

-- Create coaches view
CREATE VIEW coaches
AS
SELECT person_id AS coach_id,
       first_name,
       last_name,
       email,
       gender,
       birth_date,
       address_line_1,
       address_line_2,
       city,
       province_id,
       postal_code,
       phone,
       team_id,
       coaching_experience,
       user_password
FROM persons
WHERE coach = 'True';
GO

-- Create players view
CREATE VIEW players
AS
SELECT person_id AS player_id,
       first_name,
       last_name,
       division_id,
       email,
       gender,
       birth_date,
       address_line_1,
       address_line_2,
       city,
       province_id,
       postal_code,
       phone,
       skill_level,
       team_id,
       jersey_number,
       user_password
FROM persons
WHERE player = 'True';
GO

-- Create referees view
CREATE VIEW referees
AS
SELECT person_id AS referee_id,
       first_name,
       last_name,
       email,
       gender,
       birth_date,
       address_line_1,
       address_line_2,
       city,
       province_id,
       postal_code,
       phone,
       referee_experience,
       user_password
FROM persons
WHERE referee = 'True';
GO

